INSERT INTO `trk_posts` VALUES ('49', '1', '2016-05-18 13:01:07', '2016-05-18 13:01:07', 'Phấn trang điểm THORAKAO đã ứng dụng thật đặc biệt từ:\r\n- Dâu Tằm dưỡng da giúp cho làn da được trắng sáng.\r\n- Cám Gạo giữ ẩm cho làn da mịn màng tươi trẻ.\r\n\r\nCông dụng: Giúp da trắng sáng, thấm hút lượng dầu thừa trên bề mặt da, tạo lớp nền mỏng mịn, giữ độ ẫm cho da và bảo vệ da trước ánh nắng mặt trời.\r\n\r\nHướng dẫn sử dụng: Bạn dùng bông phấn chấm một lượng phấn vừa đủ rồi đánh đều lên khắp vùng da mặt. Muốn giử độ bền cho cà ngày bạn nên sử dụng thêm kem trang điểm THORAKAO.\r\n\r\nCó hai cách dùng:\r\n- Dùng với bông phấn ướt để tạo sự sắc nét cho gương mặt, có thể thay thế cả kem nền\r\n- Dùng với bông phấn khô để gương mặt tự nhiên và nhẹ nhàng', 'Phấn Trang Điểm Trắng Da 9g', '', 'publish', 'closed', 'closed', '', 'phan-trang-diem-trang-da-9g', '', '', '2016-06-22 08:38:19', '2016-06-22 08:38:19', '', '0', 'http://www.thorakao.com/?post_type=product&#038;p=49', '0', 'product', '', '0');
