INSERT INTO `trk_posts` VALUES ('71', '1', '2016-05-18 13:41:45', '2016-05-18 13:41:45', 'Kem lột nhẹ Nghệ Thorakao là sản phẩm ứng dụng từ củ nghệ mà dân gian đã dùng từ lâu như một dược thảo có tính sát trùng tự nhiên. Với sự phối hợp chiết xuất từ củ nghệ và các vitamin, kem lột nhẹ Nghệ sẽ:\r\n\r\n- Giúp bạn làn da sạch sẽ, mịn màng và trắng trẻo\r\n\r\n- Giúp phấn trang điểm thẩm thấu vào da thật tự nhiên.\r\n\r\nHướng dẫn sử dụng: Thoa dày một lớp kem lột nhẹ Nghệ lên da mặt, để thật khô, lột nhẹ từ cằm lên, kem sẽ kéo theo những chất bẩn, mụn cám, lông măng trên da mặt (lật ngược màng kem lên sẽ thấy rõ). Sử dụng kem lột 2 - 3 lần một tuần để duy trì làn da đầy sức sống.', 'Kem lột nhẹ Nghệ 120g', '', 'publish', 'closed', 'closed', '', 'kem-lot-nhe-nghe-120g', '', '', '2016-05-25 09:21:55', '2016-05-25 09:21:55', '', '0', 'http://www.thorakao.com/?post_type=product&#038;p=71', '0', 'product', '', '0');
